tinymce.PluginManager.add('imgupload',function(editor,url){function dialog(){editor.windowManager.open({title:'Upload an image',file:url+'/upload.htm',id:'abc',name:'abc',width:350,height:48,buttons:[{text:'Upload',classes:'widget btn primary first abs-layout-item',disabled:false,onclick:function(e){var frame=$(e.currentTarget).find("iframe").get(0);var content=frame.contentDocument;var uploadForm=$(content).find("#upload_form");uploadForm.submit();}},{text:'Close',onclick:'close'}]})}editor.addButton('imgupload',{tooltip:'Upload an image',text:'Upload Image',onclick:dialog});editor.addMenuItem('imgupload',{text:'Upload image',context:'insert',onclick: dialog})});